// src/services/notes.js
import api from "./axios.js";

// CREATE NOTE
export const createNote = (noteData) => {
  // noteData should have { title, content }
  return api.post("/notes/create", noteData);
};

// GET ALL NOTES
export const getNotes = () => {
  return api.get("/notes");
};

// UPDATE NOTE
export const updateNote = ({ id, title, content }) => {
  return api.patch(`/notes/${id}`, { title, content });
};

// DELETE NOTE
export const deleteNote = (id) => {
  // Axios delete requires `data` inside config
  return api.delete("/notes", { data: { id } });
};
