  //   text: "some text1",
  // },
  // {
  //   id: "a123",
  //   title: "some title2",
  //   text: "some text2",
  // },
];
const App = () => {
  const [notes, setNotes] = useState(NOTES);
    const [isOpenModal, set] = useState(NOTES);

  const addNote = (note) => {
    setNotes((prevNotes) => {
      return [...prevNotes, note];
    });
  };

   const deleteNote = (id) => {
    setNotes((prevNotes) => {
      return prevNotes.filter(note => id !== note.id)
    });
   };


  return (
    <div>
      <Navbar />
      <Sidebar />
      <Form addNote={addNote} />
      <Notes notes={notes} deleteNote={deleteNote} />
      <Modal />
    </div>
  );
};

export default App;
