import React, { createContext, useState, useContext } from "react";
import api from "../services/axios.js";

// Create A Context
const FormContext = createContext();
export const FormProvider = ({ children }) => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchNotes = async () => {
    setLoading(true);

    try {
      // HIT BACKEND ENDPOINT
      const res = await api.get("/notes");
      //   STORE RESPONSE IN STATE
      setNotes(res.data);
    } catch (err) {
      "Error fetching notes:", e;
    } finally {
      //   HIDE LOADING AFTER FETCH
      setLoading(false);
    }
  };
  const addNote = async ({title,text}) => {
    try {
      const res = await api.post("/notes", { title, content: text });
      setNotes((prevNotes) => [...prevNotes, res.data]);
    } catch (e) {
      console.error("Error adding note:", e);
    }
  };
  const editNote = async ({id, title, text}) => {
    try {
      const res = await api.post(`/notes${id}`, { title, content: text });
      prevNotes.map((note) => (note.id === id ? res.data : note));
    } catch (err) {
      console.error("Error editing note:", err);
    }
  };

  return (
    <FormContext.Provider
      value={{ notes, loading, fetchNotes, addNote, editNote }}
    >
      {children}
    </FormContext.Provider>
  );
};

export const useFormContext = () => useContext(FormContext);
