import "./Sidebar.css";
import { useState } from "react";

function Sidebar() {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="sidebar"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="sidebar-item active-item">
        <span className="material-symbols-outlined hover active">
          lightbulb
        </span>
        {isHovered && <span className="sidebar-text">Notes</span>}
      </div>
      <div className="sidebar-item">
        <span className="material-symbols-outlined hover">notifications</span>
        {isHovered && <span className="sidebar-text">Reminders</span>}
      </div>
      <div className="sidebar-item">
        <span className="material-symbols-outlined hover">edit</span>
        {isHovered && <span className="sidebar-text">Edit Labels</span>}
      </div>
      <div className="sidebar-item">
        <span className="material-symbols-outlined hover">archive</span>
        {isHovered && <span className="sidebar-text">Archive</span>}
      </div>
      <div className="sidebar-item">
        <span className="material-symbols-outlined hover">delete</span>
        {isHovered && <span className="sidebar-text">Trash</span>}
      </div>
    </div>
  );
}

export default Sidebar;
