import api from "./axios.js";

// Add a new note
export const createNote = (noteData) => api.post("/notes/create", noteData);

// Fetch all notes
export const getNotes = () => api.get("/notes");

// Update a note
export const updateNote = (noteData) => api.patch("/notes", noteData);

// Delete a note
export const deleteNote = (noteId) => api.delete("/notes", { data: { id: noteId } });
